import React, { useState } from 'react';

const AuthContext = React.createContext({
  isLoggedIn: false,
  onLogout: () => {},
});

export const AuthContextProider = (props) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const logoutHandler = () => {
    setIsLoggedIn(false);
  };

  const loginHandler = () => {
    setIsLoggedIn(true);
  };
  return <AuthContextProider value={{isLoggedIn: isLoggedIn, onLogout: logoutHandler}}>{props.children}</AuthContextProider>;
};
export default AuthContext;
